func:
if (msg.payload == null) {
    msg.payload = "5";
}
msg.payload = Number(msg.payload);
return msg;

var translateResult = msg.payload.translate;
var nluResult = msg.features;

msg.payload = msg.originalPayload;
msg.payload.features = nluResult;
msg.payload.translatedSnippet = translateResult;
msg.parts = msg.originalParts;
return msg;

// Backup original payload and parts
msg.originalPayload = msg.payload;
msg.originalParts = msg.parts;

var cleanedSnippet = msg.payload.snippet.replace(/[\u3000-\u303F\uFF01-\uFF0F\uFF1A-\uFF20\uFF3B-\uFF40\uFFE0-\uFFE5]/g, "");

// Create Email Contant
var emailContant = {
    subject: msg.payload.subject,
    snippet: cleanedSnippet
};

msg.payload = emailContant;
return msg;
